import React, { useState, useEffect } from 'react';
import EmployeeList from './components/EmployeeList';
import EmployeeDetails from './components/EmployeeDetails';
import AddEmployeeForm from './components/AddEmployeeForm';
import './App.css'; // Optional styling

function App() {
  const [employees, setEmployees] = useState([]);
  const [mode, setMode] = useState('list');
  const [filterDepartment, setFilterDepartment] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState(null);

  // Load employees from local storage or JSON file on mount
  useEffect(() => {
    const savedEmployees = localStorage.getItem('employees');
    if (savedEmployees) {
      setEmployees(JSON.parse(savedEmployees));
    } else {
      fetch('/employees.json')
        .then((response) => response.json())
        .then((data) => setEmployees(data))
        .catch((error) => console.error('Error loading employees:', error));
    }
  }, []);

  // Save employees to local storage whenever the list changes
  useEffect(() => {
    if (employees.length > 0) {
      localStorage.setItem('employees', JSON.stringify(employees));
    }
  }, [employees]);

  // Handle adding a new employee
  const handleAddEmployee = (newEmployee) => {
    setEmployees([...employees, newEmployee]);
    setMode('list'); // Return to list view after adding
  };

  // Handle search action
  const handleSearch = () => {
    const foundEmployee = employees.find(
      (emp) => emp.name.toLowerCase() === searchTerm.toLowerCase()
    );
    setSearchResult(foundEmployee || null);
  };

  // Sorted and filtered employee lists
  const sortedEmployees = [...employees].sort((a, b) =>
    a.name.localeCompare(b.name)
  );
  const filteredEmployees = employees.filter((emp) =>
    emp.department.toLowerCase().includes(filterDepartment.toLowerCase())
  );

  return (
    <div className="App">
      <header>
        <h1>Employee Management Tool</h1>
        <div className="controls">
          <button onClick={() => setMode('list')}>List</button>
          <button onClick={() => setMode('sort')}>Sort</button>
          <button onClick={() => setMode('filter')}>Filter by Department</button>
          <button onClick={() => setMode('search')}>Search</button>
          <button onClick={() => setMode('add')}>Add New Employee</button>
        </div>
      </header>
      <main>
        {mode === 'list' && <EmployeeList employees={employees} />}
        {mode === 'sort' && <EmployeeList employees={sortedEmployees} />}
        {mode === 'filter' && (
          <div>
            <input
              type="text"
              placeholder="Enter department"
              value={filterDepartment}
              onChange={(e) => setFilterDepartment(e.target.value)}
            />
            <EmployeeList employees={filteredEmployees} />
          </div>
        )}
        {mode === 'search' && (
          <div>
            <input
              type="text"
              placeholder="Enter name"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
            {searchResult !== null && (
              <EmployeeDetails employee={searchResult} />
            )}
          </div>
        )}
        {mode === 'add' && <AddEmployeeForm onAdd={handleAddEmployee} />}
      </main>
    </div>
  );
}

export default App;
